import { DiffPart } from "./components/DiffHighlighter";

/**
 * 时间码格式：HH:MM:SS:FF (小时:分钟:秒:帧)
 * 或 SRT格式：HH:MM:SS,mmm (小时:分钟:秒,毫秒)
 */
type Timecode = string;

/**
 * 字幕条目接口
 */
export interface Subtitle {
  /** 字幕ID */
  id: number;
  /** 起始时间码 */
  startTimecode: Timecode;
  /** 结束时间码 */
  endTimecode: Timecode;
  /** 字幕文本内容 */
  text: string;
  /** 原始字幕文本内容 */
  originalText: string;
  /** 文本差异信息 */
  diffs: DiffPart[];
  /** 是否匹配搜索条件 */
  isMatch?: boolean;
  /** 是否被修改过 */
  isModified?: boolean;
}

/**
 * 字幕轨道信息接口
 */
export interface SubtitleTrack {
  /** 轨道索引 */
  trackIndex: number;
  /** 轨道名称 */
  trackName: string;
}

/**
 * 项目信息接口
 */
export interface ProjectInfo {
  /** 项目名称 */
  projectName: string | null;
  /** 时间线名称 */
  timelineName: string | null;
}

/**
 * SRT字幕条目接口
 */
export interface SrtSubtitleEntry {
  /** 字幕ID */
  id: number;
  /** 起始时间码 */
  startTimecode: Timecode;
  /** 结束时间码 */
  endTimecode: Timecode;
  /** 字幕文本内容 */
  text: string;
}

/**
 * 导入的字幕文件接口
 */
export interface ImportedSubtitleFile {
  /** 文件名 */
  fileName: string;
  /** 字幕条目列表 */
  subtitles: SrtSubtitleEntry[];
  /** 文件元数据 */
  metadata: {
    /** 导入时间 */
    importedAt: string;
    /** 文件大小（可选） */
    fileSize?: number;
    /** 文件格式 */
    format: 'srt';
  };
}

/**
 * API错误信息接口
 */
export interface ApiError {
  /** 错误消息 */
  message: string;
  /** 错误码（可选） */
  code?: string;
}

import React from 'react';
import { styled } from '@mui/material/styles';

const InsertedText = styled('ins')(({ theme }) => ({
  backgroundColor: theme.palette.success.light,
  color: theme.palette.success.contrastText,
  textDecoration: 'none',
  padding: '2px 4px',
  borderRadius: theme.shape.borderRadius,
}));

const DeletedText = styled('del')(({ theme }) => ({
  backgroundColor: theme.palette.error.light,
  color: theme.palette.error.contrastText,
  padding: '2px 4px',
  borderRadius: theme.shape.borderRadius,
}));

export interface DiffPart {
  type: 'added' | 'removed' | 'normal';
  value: string;
}

interface DiffHighlighterProps {
  diffs: DiffPart[];
}

const DiffHighlighter: React.FC<DiffHighlighterProps> = ({ diffs }) => {
  return (
    <p>
      {diffs.map((part, index) => {
        switch (part.type) {
          case 'added':
            return <InsertedText key={index}>{part.value}</InsertedText>;
          case 'removed':
            return <DeletedText key={index}>{part.value}</DeletedText>;
          default:
            return <span key={index}>{part.value}</span>;
        }
      })}
    </p>
  );
};

export default DiffHighlighter;

import React, { useState, useRef, useEffect, memo } from 'react';
import { TableCell, TextField } from '@mui/material';
import DiffHighlighter from './DiffHighlighter';
import { Subtitle } from '../types';
import { textCellStyle, textFieldStyle, textDisplayStyle } from './sharedStyles';

interface EditableSubtitleCellProps {
  row: Subtitle;
  editingId: number | null;
  onSubtitleChange: (id: number, newText: string) => void;
  setEditingId: (id: number | null) => void;
}

// 自定义比较函数，避免使用 JSON.stringify 比较 diffs 数组
const areEqual = (prevProps: EditableSubtitleCellProps, nextProps: EditableSubtitleCellProps) => {
  // 比较基本属性
  if (
    prevProps.editingId !== nextProps.editingId ||
    prevProps.row.id !== nextProps.row.id ||
    prevProps.row.text !== nextProps.row.text ||
    prevProps.row.startTimecode !== nextProps.row.startTimecode ||
    prevProps.row.endTimecode !== nextProps.row.endTimecode ||
    prevProps.row.originalText !== nextProps.row.originalText
  ) {
    return false;
  }

  // 比较 diffs 数组
  const prevDiffs = prevProps.row.diffs;
  const nextDiffs = nextProps.row.diffs;

  if (prevDiffs === nextDiffs) {
    // 包括都为 undefined 或都为 null 的情况
    return true;
  }

  if (!prevDiffs || !nextDiffs) {
    // 其中一个为 falsy 值
    return false;
  }

  if (prevDiffs.length !== nextDiffs.length) {
    return false;
  }

  // 逐个比较 diffs 元素
  for (let i = 0; i < prevDiffs.length; i++) {
    if (
      prevDiffs[i].type !== nextDiffs[i].type ||
      prevDiffs[i].value !== nextDiffs[i].value
    ) {
      return false;
    }
  }

  return true;
};

const EditableSubtitleCell: React.FC<EditableSubtitleCellProps> = ({
  row,
  editingId,
  onSubtitleChange,
  setEditingId,
}) => {
  const [editText, setEditText] = useState(row.text);
  const textFieldRef = useRef<HTMLInputElement>(null);
  const [escapePressed, setEscapePressed] = useState(false);

  useEffect(() => {
    // Only update from prop if not currently editing that specific cell
    if (editingId !== row.id) {
      setEditText(row.text);
    }

    // 在编辑模式下全选文本
    if (editingId === row.id && textFieldRef.current) {
      // autoFocus 会处理聚焦，这里用来全选文本
      textFieldRef.current.select();
    }
  }, [row.text, editingId, row.id]);

  const isEditing = editingId === row.id;

  const handleDoubleClick = () => {
    setEditingId(row.id);
    setEditText(row.text);
    setEscapePressed(false);
  };

  const handleTextChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(event.target.value);
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter') {
      onSubtitleChange(row.id, editText);
      setEditingId(null);
    } else if (event.key === 'Escape') {
      setEscapePressed(true);
      setEditingId(null);
      setEditText(row.text);
    }
  };

  const handleBlur = () => {
    if (escapePressed) {
      setEscapePressed(false);
      return;
    }
    if (editText !== row.text) {
      onSubtitleChange(row.id, editText);
    }
    setEditingId(null);
  };

  return (
    <TableCell component="div" sx={textCellStyle} onDoubleClick={handleDoubleClick}>
      {isEditing ? (
        <TextField
          inputRef={textFieldRef}
          value={editText}
          onChange={handleTextChange}
          onKeyDown={handleKeyDown}
          onBlur={handleBlur}
          autoFocus
          fullWidth
          multiline
          variant="outlined"
          sx={textFieldStyle}
        />
      ) : (
        <div style={textDisplayStyle}>
          <DiffHighlighter diffs={row.diffs || []} />
        </div>
      )}
    </TableCell>
  );
};

export default memo(EditableSubtitleCell, areEqual);

import { render, screen, fireEvent } from '@testing-library/react';
import { describe, it, expect, vi, beforeEach } from 'vitest';
import { FillerWordRemover } from './FillerWordRemover';
import { useDataStore } from '../stores/useDataStore';
import { useSettingsStore } from '../stores/useSettingsStore';
import useNotifier from '../hooks/useNotifier';

// Mock stores and hooks
vi.mock('../stores/useDataStore');
vi.mock('../stores/useSettingsStore');
vi.mock('../hooks/useNotifier');

const mockNotify = {
  success: vi.fn(),
  error: vi.fn(),
  warning: vi.fn(),
  info: vi.fn(),
};

const mockSubtitles = [
  { id: 1, text: '那个，我想说，就是，这个很好。', originalText: '那个，我想说，就是，这个很好。', diffs: [] },
  { id: 2, text: '嗯，是的，然后我们继续。', originalText: '嗯，是的，然后我们继续。', diffs: [] },
  { id: 3, text: '这是一个正常的句子。', originalText: '这是一个正常的句子。', diffs: [] },
  { id: 4, text: '你知道吗其实这个东西很棒', originalText: '你知道吗其实这个东西很棒', diffs: [] },
];

describe('FillerWordRemover', () => {
  beforeEach(() => {
    vi.clearAllMocks();
    (useNotifier as any).mockReturnValue(mockNotify);
  });

  it('should remove filler words from subtitles', () => {
    const setSubtitles = vi.fn();
    (useDataStore as any).mockReturnValue({ subtitles: mockSubtitles, setSubtitles });
    (useSettingsStore as any).mockReturnValue({
      fillerWords: ['那个', '就是', '嗯', '然后', '其实', '你知道吗'],
      loadFillerWords: vi.fn(),
    });

    render(<FillerWordRemover />);

    const button = screen.getByRole('button', { name: /一键去口水词/i });
    fireEvent.click(button);

    expect(setSubtitles).toHaveBeenCalledWith(
      expect.arrayContaining([
        expect.objectContaining({ id: 1, text: '，我想说， 这个很好。', diffs: expect.any(Array) }),
        expect.objectContaining({ id: 2, text: '，是的， 我们继续。', diffs: expect.any(Array) }),
        expect.objectContaining({ id: 3, text: '这是一个正常的句子。' }), // No changes, so diffs might not be there
        expect.objectContaining({ id: 4, text: '这个东西很棒', diffs: expect.any(Array) }),
      ])
    );
    expect(mockNotify.success).toHaveBeenCalledWith('已成功移除所有口水词！');
  });

  it('should not change subtitles if no filler words are present', () => {
    const setSubtitles = vi.fn();
    const subtitlesWithoutFillers = [{ id: 1, text: '这是一个正常的句子。' }];
    (useDataStore as any).mockReturnValue({ subtitles: subtitlesWithoutFillers, setSubtitles });
    (useSettingsStore as any).mockReturnValue({
      fillerWords: ['啦', '吧'],
      loadFillerWords: vi.fn(),
    });

    render(<FillerWordRemover />);
    fireEvent.click(screen.getByRole('button'));

    // In the new logic, if no changes are made, setSubtitles is not called.
    // Instead, a notification is shown.
    expect(setSubtitles).not.toHaveBeenCalled();
    expect(mockNotify.info).toHaveBeenCalledWith('未发现可移除的口水词。');
  });

  it('should disable the button if there are no subtitles', () => {
    (useDataStore as any).mockReturnValue({ subtitles: [], setSubtitles: vi.fn() });
    (useSettingsStore as any).mockReturnValue({
      fillerWords: ['a', 'b'],
      loadFillerWords: vi.fn(),
    });

    render(<FillerWordRemover />);
    expect(screen.getByRole('button')).toBeDisabled();
  });

  it('should disable the button and show warning if filler words list is empty', () => {
    const setSubtitles = vi.fn();
    (useDataStore as any).mockReturnValue({ subtitles: mockSubtitles, setSubtitles });
    (useSettingsStore as any).mockReturnValue({
      fillerWords: [],
      loadFillerWords: vi.fn(),
    });

    render(<FillerWordRemover />);
    const button = screen.getByRole('button');

    expect(button).toBeDisabled();
  });

  it('should call loadFillerWords on mount if list is empty', () => {
    const loadFillerWords = vi.fn();
    (useDataStore as any).mockReturnValue({ subtitles: [], setSubtitles: vi.fn() });
    (useSettingsStore as any).mockReturnValue({
      fillerWords: [],
      loadFillerWords,
    });

    render(<FillerWordRemover />);
    expect(loadFillerWords).toHaveBeenCalledTimes(1);
  });
});

import { useEffect } from 'react';
import { Button } from '@mui/material';
import { Trash2 } from 'lucide-react';
import { useDataStore } from '../stores/useDataStore';
import { useSettingsStore } from '../stores/useSettingsStore';
import useNotifier from '../hooks/useNotifier';
import { calculateDiff } from '../utils/diff';

export function FillerWordRemover() {
  const { subtitles, setSubtitles } = useDataStore();
  const { fillerWords, loadFillerWords } = useSettingsStore();
  const notify = useNotifier();

  useEffect(() => {
    // Load filler words when the component mounts
    if (fillerWords.length === 0) {
      loadFillerWords();
    }
  }, [loadFillerWords, fillerWords.length]);

  const handleRemoveFillerWords = () => {
    if (fillerWords.length === 0) {
      notify.warning('口水词列表为空，无法执行操作。');
      return;
    }

    try {
      // For Chinese, word boundaries \b are not effective.
      // We will match the words directly.
      const fillerWordsRegex = new RegExp(fillerWords.join('|'), 'g');

      let changesMade = false;
      const updatedSubtitles = subtitles.map(subtitle => {
        // Replace filler words with an empty string and remove consecutive spaces that might result
        const cleanedText = subtitle.text.replace(fillerWordsRegex, '').replace(/\s+/g, ' ').trim();

        if (cleanedText !== subtitle.text) {
          changesMade = true;
          const newDiffs = calculateDiff(subtitle.originalText, cleanedText);
          return { ...subtitle, text: cleanedText, diffs: newDiffs };
        }
        return subtitle;
      });

      if (changesMade) {
        setSubtitles(updatedSubtitles);
        notify.success('已成功移除所有口水词！');
      } else {
        notify.info('未发现可移除的口水词。');
      }
    } catch (error) {
      console.error('Failed to remove filler words:', error);
      notify.error('移除失败，请检查控制台获取更多信息。');
    }
  };

  return (
    <Button
      variant="contained"
      startIcon={<Trash2 size={16} />}
      onClick={handleRemoveFillerWords}
      disabled={fillerWords.length === 0 || subtitles.length === 0}
    >
      一键去口水词
    </Button>
  );
}

import { render, screen, fireEvent } from '@testing-library/react';
import { vi } from 'vitest';
import FindReplace from './FindReplace';
import React from 'react';

const mockProps = {
  searchQuery: '',
  replaceQuery: '',
  showReplace: false,
  matchCase: false,
  matchWholeWord: false,
  useRegex: false,
  onSearchChange: vi.fn(),
  onReplaceChange: vi.fn(),
  onReplaceAll: vi.fn(),
  onToggleShowReplace: vi.fn(),
  onToggleMatchCase: vi.fn(),
  onToggleMatchWholeWord: vi.fn(),
  onToggleUseRegex: vi.fn(),
};

describe('FindReplace Component', () => {
  it('should render search input and toggle button', () => {
    render(<FindReplace {...mockProps} />);
    expect(screen.getByPlaceholderText('在此处搜索字幕...')).toBeInTheDocument();
    expect(screen.getByRole('button')).toBeInTheDocument();
  });

  it('should not show replace input by default', () => {
    render(<FindReplace {...mockProps} />);
    expect(screen.queryByPlaceholderText('替换为...')).not.toBeInTheDocument();
  });

  it('should show replace input when toggle is clicked', () => {
    const props = { ...mockProps, showReplace: true };
    render(<FindReplace {...props} />);
    expect(screen.getByPlaceholderText('替换为...')).toBeInTheDocument();
  });

  it('should call onToggleShowReplace when toggle button is clicked', () => {
    render(<FindReplace {...mockProps} />);
    const toggleButton = screen.getByTitle('显示高级替换');
    fireEvent.click(toggleButton);
    expect(mockProps.onToggleShowReplace).toHaveBeenCalledTimes(1);
  });

  it('should call onSearchChange when typing in search input', () => {
    render(<FindReplace {...mockProps} />);
    const searchInput = screen.getByPlaceholderText('在此处搜索字幕...');
    fireEvent.change(searchInput, { target: { value: 'test' } });
    expect(mockProps.onSearchChange).toHaveBeenCalled();
  });

  it('should call onReplaceChange when typing in replace input', () => {
    const props = { ...mockProps, showReplace: true };
    render(<FindReplace {...props} />);
    const replaceInput = screen.getByPlaceholderText('替换为...');
    fireEvent.change(replaceInput, { target: { value: 'new' } });
    expect(mockProps.onReplaceChange).toHaveBeenCalled();
  });

  it('should call onToggleMatchCase when "大小写匹配" button is clicked', () => {
    render(<FindReplace {...mockProps} />);
    const matchCaseButton = screen.getByTitle('大小写匹配');
    fireEvent.click(matchCaseButton);
    expect(mockProps.onToggleMatchCase).toHaveBeenCalledTimes(1);
  });

  it('should call onToggleMatchWholeWord when "全词匹配" button is clicked', () => {
    render(<FindReplace {...mockProps} />);
    const matchWholeWordButton = screen.getByTitle('全词匹配');
    fireEvent.click(matchWholeWordButton);
    expect(mockProps.onToggleMatchWholeWord).toHaveBeenCalledTimes(1);
  });

  it('should call onToggleUseRegex when "使用正则表达式" button is clicked', () => {
    render(<FindReplace {...mockProps} />);
    const useRegexButton = screen.getByTitle('使用正则表达式');
    fireEvent.click(useRegexButton);
    expect(mockProps.onToggleUseRegex).toHaveBeenCalledTimes(1);
  });

  it('should call onReplaceAll when "全部替换" button is clicked', () => {
    const props = { ...mockProps, showReplace: true };
    render(<FindReplace {...props} />);
    const replaceAllButton = screen.getByTitle('全部替换');
    fireEvent.click(replaceAllButton);
    expect(mockProps.onReplaceAll).toHaveBeenCalledTimes(1);
  });

  it('should call onReplaceAll when Enter is pressed in replace input', () => {
    const props = { ...mockProps, showReplace: true };
    render(<FindReplace {...props} />);
    const replaceInput = screen.getByPlaceholderText('替换为...');
    fireEvent.keyDown(replaceInput, { key: 'Enter', code: 'Enter' });
    expect(mockProps.onReplaceAll).toHaveBeenCalledTimes(1);
  });
});

import React, { memo } from 'react';
import { TextField, Box, IconButton, InputAdornment, Tooltip } from '@mui/material';
import {
  ChevronDown,
  ChevronRight,
  CaseSensitive,
  WholeWord,
  Regex,
  ReplaceAll,
} from 'lucide-react';

interface FindReplaceProps {
  searchQuery: string;
  replaceQuery: string;
  showReplace: boolean;
  matchCase: boolean;
  matchWholeWord: boolean;
  useRegex: boolean;
  onSearchChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onReplaceChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onReplaceAll: () => void;
  onToggleShowReplace: () => void;
  onToggleMatchCase: () => void;
  onToggleMatchWholeWord: () => void;
  onToggleUseRegex: () => void;
}

const FindReplace: React.FC<FindReplaceProps> = ({
  searchQuery,
  replaceQuery,
  showReplace,
  matchCase,
  matchWholeWord,
  useRegex,
  onSearchChange,
  onReplaceChange,
  onReplaceAll,
  onToggleShowReplace,
  onToggleMatchCase,
  onToggleMatchWholeWord,
  onToggleUseRegex,
}) => {
  const handleReplaceKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      onReplaceAll();
    }
  };

  const commonInputStyles = {
    '& .MuiOutlinedInput-root': {
      backgroundColor: '#3c3c3c',
      color: '#cccccc',
      fontSize: '0.8rem',
      height: '28px',
      '& fieldset': {
        borderColor: '#464647',
      },
      '&:hover fieldset': {
        borderColor: '#464647',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#007acc',
      },
    },
    '& .MuiOutlinedInput-input': {
      '&::placeholder': {
        color: '#969696',
        opacity: 1,
      },
    },
  };

  const iconButtonStyles = (isActive: boolean) => ({
    width: 24,
    height: 24,
    borderRadius: '4px',
    color: '#cccccc',
    backgroundColor: isActive ? '#094771' : 'transparent',
    '&:hover': {
      backgroundColor: isActive ? '#094771' : '#464647',
    },
  });

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.25, position: 'relative' }}>
      {/* Toggle Button - Positioned absolutely */}
      <Tooltip title={showReplace ? "隐藏替换" : "显示替换"}>
        <IconButton
          onClick={onToggleShowReplace}
          size="small"
          sx={{
            color: '#cccccc',
            padding: '1px',
            width: '18px',
            height: '18px',
            position: 'absolute',
            left: 0,
            top: showReplace ? 'calc(26px + 0.125rem - 9px)' : '4px', // Center align with the search input when collapsed
            zIndex: 1, // Ensure it's above other elements
            transition: 'top 0.2s ease-in-out', // Smooth transition when expanding/collapsing
          }}
        >
          {showReplace ? <ChevronDown size={14} /> : <ChevronRight size={14} />}
        </IconButton>
      </Tooltip>

      {/* Search Input */}
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.25, paddingLeft: '22px' /* Space for the button */ }}>
        <TextField
          placeholder="搜索"
          variant="outlined"
          size="small"
          value={searchQuery}
          onChange={onSearchChange}
          fullWidth
          sx={{
            ...commonInputStyles,
            '& .MuiOutlinedInput-root': {
              ...commonInputStyles['& .MuiOutlinedInput-root'],
              width: '100%',
              height: '26px',
            }
          }}
          InputProps={{
            sx: { height: '26px', minHeight: '26px' },
            endAdornment: (
              <InputAdornment position="end" sx={{ gap: 0.25 }}>
                <Tooltip title="大小写匹配">
                  <IconButton sx={{...iconButtonStyles(matchCase), width: 24, height: 24, padding: '2px'}} onClick={onToggleMatchCase}>
                    <CaseSensitive size={16} />
                  </IconButton>
                </Tooltip>
                <Tooltip title="全词匹配">
                  <IconButton sx={{...iconButtonStyles(matchWholeWord), width: 24, height: 24, padding: '2px'}} onClick={onToggleMatchWholeWord}>
                    <WholeWord size={16} />
                  </IconButton>
                </Tooltip>
                <Tooltip title="使用正则表达式">
                  <IconButton sx={{...iconButtonStyles(useRegex), width: 24, height: 24, padding: '2px'}} onClick={onToggleUseRegex}>
                    <Regex size={16} />
                  </IconButton>
                </Tooltip>
              </InputAdornment>
            ),
          }}
        />
      </Box>

      {/* Replace Input */}
      {showReplace && (
        <Box sx={{ display: 'flex', alignItems: 'center', paddingLeft: '22px' /* Space for the button */ }}>
          <TextField
            placeholder="替换"
            variant="outlined"
            size="small"
            value={replaceQuery}
            onChange={onReplaceChange}
            onKeyDown={handleReplaceKeyDown}
            fullWidth
            sx={{
              ...commonInputStyles,
              '& .MuiOutlinedInput-root': {
                ...commonInputStyles['& .MuiOutlinedInput-root'],
                height: '26px',
              }
            }}
            InputProps={{
              sx: { height: '26px', minHeight: '26px' },
              endAdornment: (
                <InputAdornment position="end">
                  <Tooltip title="全部替换">
                    <IconButton sx={{...iconButtonStyles(false), width: 24, height: 24, padding: '2px'}} onClick={onReplaceAll}>
                      <ReplaceAll size={16} />
                    </IconButton>
                  </Tooltip>
                </InputAdornment>
              ),
            }}
          />
        </Box>
      )}
    </Box>
  );
};

export default memo(FindReplace);

import React from 'react';
