import { memo, forwardRef } from 'react';
import { Box } from '@mui/material';
import { Subtitle } from '../types';
import {
  idCellStyle,
  timecodeCellStyle,
} from './sharedStyles';
import EditableSubtitleCell from './EditableSubtitleCell';

interface SubtitleRowProps {
  row: Subtitle;
  selectedRow: number | null;
  editingId: number | null;
  handleRowClick: (startTimecode: string, endTimecode: string, id: number) => void;
  onSubtitleChange: (id: number, newText: string) => void;
  setEditingId: (id: number | null) => void;
}

// 自定义比较函数，避免比较函数引用
const areEqual = (prevProps: SubtitleRowProps, nextProps: SubtitleRowProps) => {
  // 比较非函数属性
  return (
    prevProps.row.id === nextProps.row.id &&
    prevProps.row.startTimecode === nextProps.row.startTimecode &&
    prevProps.row.endTimecode === nextProps.row.endTimecode &&
    prevProps.row.text === nextProps.row.text &&
    prevProps.row.originalText === nextProps.row.originalText &&
    // 比较 diffs 数组
    prevProps.row.diffs === nextProps.row.diffs && // 在 JavaScript 中，对象引用比较是合理的，因为 diffs 应该是不可变的
    prevProps.selectedRow === nextProps.selectedRow &&
    prevProps.editingId === nextProps.editingId
  );
};

const SubtitleRow = memo(forwardRef<HTMLDivElement, SubtitleRowProps>(({
  row,
  selectedRow,
  editingId,
  handleRowClick,
  onSubtitleChange,
  setEditingId,
}, ref) => {
  // 直接使用从父组件传递过来的row数据
  const currentSubtitle = row;

  // 处理整行双击编辑
  const handleRowDoubleClick = () => {
    setEditingId(row.id);
  };

  return (
    <Box
      ref={ref}
      key={currentSubtitle.id}
      onClick={() => handleRowClick(currentSubtitle.startTimecode, currentSubtitle.endTimecode, currentSubtitle.id)}
      onDoubleClick={handleRowDoubleClick}
      sx={{
        display: 'flex',
        alignItems: 'center',
        height: '50px',
        borderBottom: 1,
        borderColor: 'divider',
        cursor: 'pointer',
        '&:hover': {
          backgroundColor: 'action.hover',
        },
        ...(selectedRow === currentSubtitle.id && {
          backgroundColor: 'action.selected',
        }),
      }}
    >
      <Box component="div" sx={{ ...idCellStyle, textAlign: 'center', py: 0.25 }}>
        {currentSubtitle.id}
      </Box>
      <Box component="div" sx={{ ...timecodeCellStyle, textAlign: 'center', py: 0.25 }}>
        {currentSubtitle.startTimecode}
      </Box>
      <Box component="div" sx={{ ...timecodeCellStyle, textAlign: 'center', py: 0.25 }}>
        {currentSubtitle.endTimecode}
      </Box>
      <EditableSubtitleCell
        row={currentSubtitle}
        editingId={editingId}
        onSubtitleChange={onSubtitleChange}
        setEditingId={setEditingId}
      />
    </Box>
  );
}));

export default memo(SubtitleRow, areEqual);

import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import { vi } from 'vitest';
import '@testing-library/jest-dom';
import SubtitleTable from './SubtitleTable';
import { Subtitle } from '../types';

// Mock the useNotifier hook
vi.mock('../hooks/useNotifier', () => ({
  __esModule: true,
  default: () => ({
    error: vi.fn(),
    success: vi.fn(),
  }),
}));

interface MockListProps {
  children: React.ComponentType<{ data: any; index: number; style: React.CSSProperties; key: number }>;
  itemData: {
    subtitles: { id: number }[];
  };
  height: number;
  width: string;
}

// Mock react-window
vi.mock('react-window', () => ({
  __esModule: true,
  FixedSizeList: ({ children: RowComponent, itemData, height, width }: MockListProps) => {
    return (
      <div style={{ height, width }}>
        {itemData.subtitles.map((subtitle, index) => (
          <RowComponent data={itemData} index={index} style={{}} key={subtitle.id} />
        ))}
      </div>
    );
  },
}));


describe('SubtitleTable', () => {
  const mockSubtitles: Subtitle[] = [
    { id: 1, startTimecode: '00:00:01,000', endTimecode: '00:00:03,000', text: 'Hello world', originalText: 'Hello world', diffs: [{ type: 'normal', value: 'Hello world' }] },
    { id: 2, startTimecode: '00:00:04,000', endTimecode: '00:00:06,000', text: 'This is a test', originalText: 'This is a test', diffs: [{ type: 'normal', value: 'This is a test' }] },
  ];

  const mockOnSubtitleChange = vi.fn();

  beforeEach(() => {
    vi.clearAllMocks();
    // Mock fetch
    globalThis.fetch = vi.fn(() =>
      Promise.resolve({
        ok: true,
        json: () => Promise.resolve({}),
      })
    ) as any;
  });

  it('renders subtitles correctly', () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    expect(screen.getByText('Hello world')).toBeInTheDocument();
    expect(screen.getByText('This is a test')).toBeInTheDocument();
  });

  it('renders a filtered list of subtitles correctly', () => {
    const filteredSubtitles = [mockSubtitles[0]]; // Only "Hello world"
    render(<SubtitleTable subtitles={filteredSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);

    expect(screen.getByText('Hello world')).toBeInTheDocument();
    expect(screen.queryByText('This is a test')).not.toBeInTheDocument();
  });

  it('enters edit mode on double click', async () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    const textElement = screen.getByText('Hello world');
    fireEvent.doubleClick(textElement);
    const input = await screen.findByDisplayValue('Hello world');
    expect(input).toBeInTheDocument();
  });

  it('allows text to be changed in edit mode', async () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    fireEvent.doubleClick(screen.getByText('Hello world'));
    const input: HTMLInputElement = await screen.findByDisplayValue('Hello world');
    fireEvent.change(input, { target: { value: 'Updated text' } });
    expect(input.value).toBe('Updated text');
  });

  it('calls onSubtitleChange and exits edit mode on Enter key', async () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    fireEvent.doubleClick(screen.getByText('Hello world'));
    const input = await screen.findByDisplayValue('Hello world');
    fireEvent.change(input, { target: { value: 'Updated text' } });
    fireEvent.keyDown(input, { key: 'Enter', code: 'Enter' });

    expect(mockOnSubtitleChange).toHaveBeenCalledWith(1, 'Updated text');
    expect(screen.queryByDisplayValue('Updated text')).not.toBeInTheDocument();
  });

  it('calls onSubtitleChange and exits edit mode on blur', async () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    fireEvent.doubleClick(screen.getByText('Hello world'));
    const input = await screen.findByDisplayValue('Hello world');
    fireEvent.change(input, { target: { value: 'Updated on blur' } });
    fireEvent.blur(input);

    expect(mockOnSubtitleChange).toHaveBeenCalledWith(1, 'Updated on blur');
    expect(screen.queryByDisplayValue('Updated on blur')).not.toBeInTheDocument();
  });

  it('does not call onSubtitleChange and exits edit mode on Escape key', async () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    fireEvent.doubleClick(screen.getByText('Hello world'));
    const input = await screen.findByDisplayValue('Hello world');
    fireEvent.change(input, { target: { value: 'This should not be saved' } });
    fireEvent.keyDown(input, { key: 'Escape', code: 'Escape' });

    expect(mockOnSubtitleChange).not.toHaveBeenCalled();
    expect(screen.queryByDisplayValue('This should not be saved')).not.toBeInTheDocument();
    // The original text should be back
    expect(screen.getByText('Hello world')).toBeInTheDocument();
  });

  it('does not call onSubtitleChange on blur after Escape key is pressed', async () => {
    render(<SubtitleTable subtitles={mockSubtitles} jumpTo="start" onSubtitleChange={mockOnSubtitleChange} />);
    fireEvent.doubleClick(screen.getByText('Hello world'));
    const input = await screen.findByDisplayValue('Hello world');
    fireEvent.change(input, { target: { value: 'This should also not be saved' } });

    // Press Escape
    fireEvent.keyDown(input, { key: 'Escape', code: 'Escape' });

    // Then blur the input
    fireEvent.blur(input);

    // The change should NOT have been saved
    expect(mockOnSubtitleChange).not.toHaveBeenCalled();
    expect(screen.queryByDisplayValue('This should also not be saved')).not.toBeInTheDocument();
    expect(screen.getByText('Hello world')).toBeInTheDocument();
  });
});

import React, { useCallback, memo, useState, useRef, useEffect, useMemo } from 'react';
import { Box, styled } from '@mui/material';
import { Virtuoso, VirtuosoHandle } from 'react-virtuoso';
import SubtitleRow from './SubtitleRow';
import { idCellStyle, timecodeCellStyle, textCellStyle, scrollbarStyle } from './sharedStyles';
import { useTimelineNavigation } from '../hooks/useTimelineNavigation';
import { useDataStore } from '../stores/useDataStore';
import { useUIStore } from '../stores/useUIStore';

interface SubtitleTableProps {
  jumpToSubtitleId: number | null;
  onRowClick: (id: number) => void;
}

// 自定义标记层组件，用于在滚动条上显示修改过的行
const ModifiedLinesOverlay = styled('div')({
  position: 'absolute',
  top: 0,
  right: 0,
  width: '10px', // 滚动条宽度
  height: '100%',
  pointerEvents: 'none', // 不拦截鼠标事件
  zIndex: 1000, // 确保在滚动条上方
});

// 修改标记组件
const ModifiedLineMarker = styled('div')(() => ({
  position: 'absolute',
  right: '2px', // 距离右边 2px
  width: '6px', // 宽度 6px
  height: '2px', // 高度 2px
  backgroundColor: '#FFCC00', // VSCode 中修改行的黄色标记
  borderRadius: '1px', // 轻微圆角
}));

const SubtitleTable: React.FC<SubtitleTableProps> = ({ jumpToSubtitleId, onRowClick }) => {
  const virtuosoRef = useRef<VirtuosoHandle>(null);
  const { setTimecode } = useTimelineNavigation();
  const [selectedRow, setSelectedRow] = useState<number | null>(null);
  const [editingId, setEditingId] = useState<number | null>(null);
  const subtitles = useDataStore((state) => state.subtitles);
  const updateSubtitleText = useDataStore((state) => state.updateSubtitleText);
  const modifiedIndices = useDataStore((state) => state.getModifiedSubtitleIndices());
  const jumpTo = useUIStore((state) => state.jumpTo);

  // 使用 useCallback 包装回调函数以稳定它们的引用
  const handleRowClick = useCallback(async (inPoint: string, outPoint: string, id: number) => {
    setSelectedRow(id);
    onRowClick(id);
    await setTimecode(inPoint, outPoint, jumpTo);
  }, [jumpTo, setTimecode, onRowClick]);

  const handleSubtitleChange = useCallback((id: number, newText: string) => {
    updateSubtitleText(id, newText);
  }, [updateSubtitleText]);

  const handleSetEditingId = useCallback((id: number | null) => {
    setEditingId(id);
  }, []);

  useEffect(() => {
    if (jumpToSubtitleId !== null) {
      const index = subtitles.findIndex(s => s.id === jumpToSubtitleId);
      if (index !== -1 && virtuosoRef.current) {
        virtuosoRef.current.scrollToIndex({
          index,
          align: 'center',
          behavior: 'smooth',
        });
        const sub = subtitles[index];
        handleRowClick(sub.startTimecode, sub.endTimecode, sub.id);
      }
    }
  }, [jumpToSubtitleId, subtitles, handleRowClick]);

  const rowContent = useCallback((_index: number, subtitle: (typeof subtitles)[number]) => {
    return (
      <div key={subtitle.id}>
        <SubtitleRow
          row={subtitle}
          selectedRow={selectedRow}
          editingId={editingId}
          handleRowClick={handleRowClick}
          onSubtitleChange={handleSubtitleChange}
          setEditingId={handleSetEditingId}
        />
      </div>
    );
  }, [subtitles, selectedRow, editingId, handleRowClick, handleSubtitleChange, handleSetEditingId]);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      {/* Header */}
      <Box sx={{ display: 'flex', flexShrink: 0, borderBottom: 1, borderColor: 'divider' }}>
        <Box sx={{ ...idCellStyle, textAlign: 'center', py: 1 }}>序号</Box>
        <Box sx={{ ...timecodeCellStyle, textAlign: 'center', py: 1 }}>起始时间码</Box>
        <Box sx={{ ...timecodeCellStyle, textAlign: 'center', py: 1 }}>结束时间码</Box>
        <Box sx={{ ...textCellStyle, py: 1, pl: 2 }}>字幕内容</Box>
      </Box>

      {/* Body */}
      <Box sx={{ flex: 1, ...scrollbarStyle, position: 'relative' }}>
        <Virtuoso
          ref={virtuosoRef}
          style={{ height: '100%' }}
          data={subtitles}
          itemContent={rowContent}
        />
        {/* 修改过的行标记层 */}
        <ModifiedLinesOverlay>
          {useMemo(() => {
            const totalLines = subtitles.length;
            if (totalLines === 0) return null;

            return modifiedIndices.map((index) => {
              const position = (index / totalLines) * 100;
              return (
                <ModifiedLineMarker
                  key={subtitles[index].id}
                  style={{
                    top: `${position}%`,
                  }}
                />
              );
            });
          }, [modifiedIndices, subtitles.length])}
        </ModifiedLinesOverlay>
      </Box>
    </Box>
  );
};

export default memo(SubtitleTable);

import { Box, IconButton, Tooltip, Paper } from '@mui/material';
import { Files, Search, Upload, Wand2 } from 'lucide-react';
import { useUIStore } from '../../stores/useUIStore';

export function ActivityBar() {
  const { activeView, setActiveView, isSidebarOpen, toggleSidebar } = useUIStore();

  const items = [
    { id: 'explorer', icon: Files, label: 'Explorer' },
    { id: 'search', icon: Search, label: 'Search' },
    { id: 'optimizer', icon: Wand2, label: 'Optimizer' },
    { id: 'git', icon: Upload, label: 'Export' },
  ];

  const handleViewChange = (viewId: string) => {
    if (activeView === viewId) {
      // 点击相同图标：切换 Sidebar 的收纳/展示状态
      toggleSidebar();
    } else {
      // 点击不同图标：切换视图并确保 Sidebar 展开
      setActiveView(viewId);
      if (!isSidebarOpen) {
        toggleSidebar();
      }
    }
  };

  return (
    <Paper
      sx={{
        width: 48,
        height: '100%',
        backgroundColor: '#2c2c2c',
        borderRadius: 0,
        display: 'flex',
        flexDirection: 'column',
        borderRight: '1px solid #3c3c3c',
      }}
    >
      <Box sx={{ display: 'flex', flexDirection: 'column' }}>
        {items.map((item) => (
          <Tooltip key={item.id} title={item.label} placement="right">
            <IconButton
              onClick={() => handleViewChange(item.id)}
              sx={{
                height: 48,
                width: 48,
                borderRadius: 0,
                color: '#cccccc',
                backgroundColor: activeView === item.id ? '#37373d' : 'transparent',
                borderRight: activeView === item.id ? '2px solid #007acc' : 'none',
                '&:hover': {
                  backgroundColor: '#37373d',
                },
              }}
            >
              <item.icon size={20} />
            </IconButton>
          </Tooltip>
        ))}
      </Box>
    </Paper>
  );
}

import {
  Box,
  Typography,
  List,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Paper,
  IconButton,
  Snackbar,
  Alert
} from '@mui/material';
import { InsertDriveFile, Description } from '@mui/icons-material';
import { RefreshCcw, Import } from 'lucide-react';
import { useDataStore } from '../../stores/useDataStore';
import { useSrtImporter } from '../../hooks/useSrtImporter';
import { useState } from 'react';
import { Subtitle } from '../../types';
import { convertSrtToSubtitles } from '../../utils/converter';

// 定义选择状态类型
type SelectionType = 'track' | 'importedFile' | null;

interface SelectionState {
  type: SelectionType;
  id: string | number; // 对于 track 是 trackIndex，对于 importedFile 是 fileName
}

interface FileExplorerProps {
  fetchSubtitleTracks: () => Promise<void>;
  loading: boolean;
  onTrackSelect: (trackIndex: number) => void;
}

export function FileExplorer({
  fetchSubtitleTracks,
  loading,
  onTrackSelect,
}: FileExplorerProps) {
  const subtitleTracks = useDataStore((state) => state.subtitleTracks);
  const setSubtitles = useDataStore((state) => state.setSubtitles);
  const importedSubtitleFiles = useDataStore((state) => state.importedSubtitleFiles);
  const [selection, setSelection] = useState<SelectionState>({ type: null, id: 0 });

  // 判断是否选中的逻辑
  const isSelected = (type: SelectionType, id: string | number) => {
    return selection.type === type && selection.id === id;
  };

  // 使用自定义Hook处理SRT文件导入
  const {
    handleFileChange,
    isImporting,
    triggerFileSelect,
    fileInputRef,
    snackbarOpen,
    snackbarMessage,
    snackbarSeverity,
    handleSnackbarClose,
  } = useSrtImporter();

  return (
    <Paper
      sx={{
        height: '100%',
        backgroundColor: '#252526',
        borderRadius: 0,
        borderRight: '1px solid #3c3c3c',
      }}
    >
      <Box sx={{ p: 1.5, borderBottom: '1px solid #3c3c3c', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h6" sx={{ color: '#cccccc' }}>
          字幕源
        </Typography>
        <Box sx={{ display: 'flex', gap: 0.5 }}>
          <IconButton onClick={fetchSubtitleTracks} disabled={loading} size="small">
            <RefreshCcw size={16} color={loading ? "#666" : "#ccc"} />
          </IconButton>
          <IconButton onClick={triggerFileSelect} disabled={isImporting} size="small" title="导入SRT文件">
            <Import size={16} color={isImporting ? "#666" : "#ccc"} />
          </IconButton>
        </Box>
      </Box>

      {/* 隐藏的文件输入元素 */}
      <input
        type="file"
        ref={fileInputRef}
        onChange={handleFileChange}
        accept=".srt"
        style={{ display: 'none' }}
      />
      <List dense sx={{ py: 1 }}>
        {/* 显示导入的SRT文件 */}
        {importedSubtitleFiles.length > 0 && (
          <>
            <Box sx={{ px: 2, py: 0.5 }}>
              <Typography variant="caption" sx={{ color: '#888888' }}>
                导入的文件
              </Typography>
            </Box>
            {importedSubtitleFiles.map((file) => (
              <ListItemButton
                key={`imported-${file.fileName}`}
                onClick={() => {
                  // 当点击导入的文件时，将其字幕数据设置为当前显示的字幕
                  const convertedSubtitles: Subtitle[] = convertSrtToSubtitles(file.subtitles);
                  setSubtitles(convertedSubtitles);
                  setSelection({ type: 'importedFile', id: file.fileName });
                }}
                selected={isSelected('importedFile', file.fileName)}
                sx={{
                  paddingLeft: `16px`,
                  paddingY: 0.25,
                  minHeight: 28,
                  color: '#cccccc',
                  '&:hover': {
                    backgroundColor: '#37373d',
                  },
                  '&.Mui-selected': {
                    backgroundColor: '#37373d',
                    '&:hover': {
                      backgroundColor: '#37373d',
                    },
                  },
                }}
              >
                <ListItemIcon sx={{ minWidth: 24, color: '#4ec9b0' }}>
                  <Description sx={{ fontSize: 16 }} />
                </ListItemIcon>
                <ListItemText
                  primary={`[导入] ${file.fileName}`}
                  primaryTypographyProps={{ fontSize: '0.75rem' }}
                />
              </ListItemButton>
            ))}
          </>
        )}

        {/* 显示时间线轨道 */}
        {subtitleTracks.length > 0 && (
          <>
            <Box sx={{ px: 2, py: 0.5, mt: importedSubtitleFiles.length > 0 ? 1 : 0 }}>
              <Typography variant="caption" sx={{ color: '#888888' }}>
                时间线轨道
              </Typography>
            </Box>

            {subtitleTracks.map(track => (
              <ListItemButton
                key={track.trackIndex}
                onClick={() => {
                  onTrackSelect(track.trackIndex);
                  setSelection({ type: 'track', id: track.trackIndex });
                }}
                selected={isSelected('track', track.trackIndex)}
                sx={{
                  paddingLeft: `16px`,
                  paddingY: 0.25,
                  minHeight: 28,
                  color: '#cccccc',
                  '&:hover': {
                    backgroundColor: '#37373d',
                  },
                  '&.Mui-selected': {
                    backgroundColor: '#37373d',
                    '&:hover': {
                      backgroundColor: '#37373d',
                    },
                  },
                }}
              >
                <ListItemIcon sx={{ minWidth: 24, color: '#519aba' }}>
                  <InsertDriveFile sx={{ fontSize: 16 }} />
                </ListItemIcon>
                <ListItemText
                  primary={`[轨道] ${track.trackName}`}
                  primaryTypographyProps={{ fontSize: '0.75rem' }}
                />
              </ListItemButton>
            ))}
          </>
        )}
      </List>

      {/* 当没有字幕源时显示提示信息 */}
      {importedSubtitleFiles.length === 0 && subtitleTracks.length === 0 && (
        <Box sx={{ p: 2, textAlign: 'center', color: '#888888' }}>
          <Typography variant="body2">
            暂无字幕源
          </Typography>
          <Typography variant="caption" sx={{ mt: 1, display: 'block' }}>
            点击上方按钮导入SRT文件或刷新时间线轨道
          </Typography>
        </Box>
      )}

      {/* 导入结果提示 */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleSnackbarClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={handleSnackbarClose}
          severity={snackbarSeverity}
          variant="filled"
          sx={{ width: '100%' }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Paper>
  );
}

import { useState } from 'react';
import { Box, Typography, Menu, MenuItem } from '@mui/material';
import { KeyboardArrowDown } from '@mui/icons-material';
import { useUIStore } from '../../stores/useUIStore';
import type { JumpTo } from '../../stores/useUIStore';

const JUMP_MODE_MAP: Record<JumpTo, string> = {
  start: '入点',
  middle: '中点',
  end: '出点',
};

const JUMP_MODE_COLOR_MAP: Record<JumpTo, string> = {
  start: '#4ade80', // Green
  middle: '#facc15', // Yellow
  end: '#f87171',   // Red
};

const JUMP_MODE_ORDER: JumpTo[] = ['start', 'middle', 'end'];

export function JumpModeSelector() {
  const jumpTo = useUIStore((state) => state.jumpTo);
  const setJumpTo = useUIStore((state) => state.setJumpTo);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleMenuItemClick = (mode: JumpTo) => {
    setJumpTo(mode);
    handleClose();
  };

  return (
    <div>
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
        <Typography
          variant="caption"
          sx={{ fontSize: '0.6rem', color: '#9ca3af' }}
        >
          播放头跳转:
        </Typography>
        <Box
          id="jump-mode-button"
          aria-controls={open ? 'jump-mode-menu' : undefined}
          aria-haspopup="true"
          aria-expanded={open ? 'true' : undefined}
          onClick={handleClick}
          sx={{
            display: 'flex',
            alignItems: 'center',
            gap: 0.5,
            cursor: 'pointer',
            backgroundColor: '#313131',
            border: '1px solid #242424',
            paddingX: 1,
            paddingY: 0,
            borderRadius: 1,
            transition: 'background-color 0.2s',
            '&:hover': {
              backgroundColor: '#424242',
            },
          }}
        >
          <Typography
            variant="caption"
            sx={{
              fontSize: '0.6rem',
              fontWeight: 'medium',
              color: JUMP_MODE_COLOR_MAP[jumpTo],
            }}
          >
            {JUMP_MODE_MAP[jumpTo]}
          </Typography>
          <KeyboardArrowDown sx={{ fontSize: 12, color: '#9ca3af' }} />
        </Box>
      </Box>
      <Menu
        id="jump-mode-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'jump-mode-button',
        }}
        PaperProps={{
          sx: {
            backgroundColor: '#3c3c3c',
            color: '#cccccc',
            border: '1px solid #555',
          },
        }}
      >
        {JUMP_MODE_ORDER.map((mode) => (
          <MenuItem
            key={mode}
            selected={mode === jumpTo}
            onClick={() => handleMenuItemClick(mode)}
            sx={{
              fontSize: '0.8rem',
              minWidth: 60,
              display: 'flex',
              justifyContent: 'center',
              color: JUMP_MODE_COLOR_MAP[mode],
              '&.Mui-selected': {
                fontWeight: 'bold',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                '&:hover': {
                  backgroundColor: 'rgba(255, 255, 255, 0.15)',
                },
              },
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.08)',
              },
            }}
          >
            {JUMP_MODE_MAP[mode]}
          </MenuItem>
        ))}
      </Menu>
    </div>
  );
}

import { useState, useCallback, useRef, useEffect } from 'react';
import { Box, Paper, Typography, Button } from '@mui/material';
import { Download, Send } from 'lucide-react';
import { ThemeProvider } from './ThemeProvider';
import { ActivityBar } from './ActivityBar';
import { FileExplorer } from './FileExplorer';
import { OptimizerSidebar } from './OptimizerSidebar';
import { SubtitleEditorPage } from '../../pages/SubtitleEditorPage';
import { StatusBar } from './StatusBar';
import { TitleBar } from './TitleBar';
import FindReplace from '../FindReplace';
import SearchResults from '../SearchResults';
import { useFindReplace } from '../../hooks/useFindReplace';
import { Subtitle } from '../../types';
import { useUIStore } from '../../stores/useUIStore';
import { useDataStore } from '../../stores/useDataStore';
import useNotifier from '../../hooks/useNotifier';

export function MainLayout() {
  const notify = useNotifier();
  const {
    activeView,
    setActiveView,
    sidebarWidth,
    isSidebarOpen,
    setSidebarWidth,
    activeTrackIndex,
    setActiveTrackIndex,
    setSelectedSubtitleId,
  } = useUIStore();
  const {
    setSubtitles,
    setFrameRate,
    setConnectionStatus,
    setErrorMessage,
    setProjectInfo,
    setSubtitleTracks,
    handleExport,
    handleExportToDavinci,
    subtitles,
    errorMessage: currentErrorMessage,
  } = useDataStore();

  const [loading, setLoading] = useState(false);
  const [jumpToSubtitleId, setJumpToSubtitleId] = useState<number | null>(null);
  const abortControllerRef = useRef<AbortController | null>(null);

  const fetchSubtitles = useCallback(async (trackIndex: number = 1) => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    abortControllerRef.current = new AbortController();
    const signal = abortControllerRef.current.signal;

    setLoading(true);
    setConnectionStatus("connecting");
    setErrorMessage(null);
    try {
      const response = await fetch(`${import.meta.env.VITE_API_BASE_URL}/api/v1/subtitles?track_index=${trackIndex}`, { signal });
      const data = await response.json();

      if (response.ok && data.status === "success") {
        const subtitlesWithDiffs = data.data.map((sub: any) => ({
          ...sub,
          originalText: sub.text,
          diffs: [{ type: "normal", value: sub.text }],
        }));
        setSubtitles(subtitlesWithDiffs);
        setFrameRate(data.frameRate);
        setConnectionStatus("connected");
      } else {
        const errorPayload = {
          message: data.message || "获取字幕失败",
          code: data.code,
        };
        setErrorMessage(errorPayload);
        throw new Error(errorPayload.message);
      }
    } catch (error: any) {
      if (error.name === 'AbortError') {
        console.log('Fetch aborted');
        return;
      }
      setConnectionStatus("error");
      if (!currentErrorMessage) {
        setErrorMessage({
          message: error.message || "无法连接到后端服务，请检查服务是否正在运行。",
        });
      }
      setSubtitles([]);
    } finally {
      setLoading(false);
    }
  }, [setSubtitles, setFrameRate, setConnectionStatus, setErrorMessage]);

  const fetchProjectInfo = useCallback(async () => {
    try {
      const response = await fetch(`${import.meta.env.VITE_API_BASE_URL}/api/v1/project-info`);
      const result = await response.json();

      if (response.ok && result.status === 'success') {
        setProjectInfo(result.data);
      } else {
        // Don't throw an error, just log it, as this is non-critical info
        console.error(result.message || '获取项目信息失败');
        setProjectInfo({ projectName: 'N/A', timelineName: 'N/A' });
      }
    } catch (error) {
      console.error('Failed to fetch project info:', error);
      setProjectInfo({ projectName: 'N/A', timelineName: 'N/A' });
    }
  }, [setProjectInfo]);

  const fetchSubtitleTracks = useCallback(async () => {
    setLoading(true);
    setErrorMessage(null);
    setConnectionStatus("connecting");
    try {
      const response = await fetch(`${import.meta.env.VITE_API_BASE_URL}/api/v1/timeline/subtitle_tracks`);
      const data = await response.json();
      if (response.ok && data.status === "success") {
        setSubtitleTracks(data.data);
        if (data.data.length > 0) {
          const firstTrackIndex = data.data[0].track_index;
          setActiveTrackIndex(firstTrackIndex);
          // The useEffect hook will trigger fetchSubtitles when activeTrackIndex is set.
          // We only need to fetch non-critical project info here.
          await fetchProjectInfo();
        } else {
          // No tracks, but still connected
          setConnectionStatus("connected");
          setSubtitles([]);
          setActiveTrackIndex(null);
        }
      } else {
        const errorPayload = {
          message: data.message || "获取字幕轨道失败",
          code: data.code,
        };
        setErrorMessage(errorPayload);
        throw new Error(errorPayload.message);
      }
    } catch (error: any) {
      if (!currentErrorMessage) {
        setErrorMessage({ message: error.message || "无法获取字幕轨道" });
      }
      setConnectionStatus("error");
      setSubtitles([]);
    } finally {
      setLoading(false);
    }
  }, [setErrorMessage, setConnectionStatus, setSubtitleTracks, setActiveTrackIndex, fetchProjectInfo, setSubtitles, currentErrorMessage]);

  useEffect(() => {
    if (activeTrackIndex !== null) {
      fetchSubtitles(activeTrackIndex);
    }
    // We don't include fetchSubtitles in the dependency array because
    // it's a stable function from useCallback, but including it
    // satisfies the exhaustive-deps lint rule and is good practice.
  }, [activeTrackIndex, fetchSubtitles]);

  const handleTrackSelect = useCallback((trackIndex: number) => {
    setActiveTrackIndex(trackIndex);
  }, []);

  const handleResultClick = (subtitle: Subtitle) => {
    setJumpToSubtitleId(subtitle.id);
    // Reset after a short delay to allow for re-clicking the same item
    setTimeout(() => setJumpToSubtitleId(null), 50);
  };

  const handleRowClick = useCallback((id: number) => {
    setSelectedSubtitleId(id);
  }, [setSelectedSubtitleId]);

  const {
    searchQuery,
    replaceQuery,
    showReplace,
    matchCase,
    matchWholeWord,
    useRegex,
    handleSearchChange,
    handleReplaceChange,
    toggleShowReplace,
    toggleMatchCase,
    toggleMatchWholeWord,
    toggleUseRegex,
    handleReplaceAll,
    filteredSubtitles,
  } = useFindReplace();
  const isResizing = useRef(false);
  const dragStartInfo = useRef<{ startX: number; startWidth: number } | null>(null);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    isResizing.current = true;
    dragStartInfo.current = {
      startX: e.clientX,
      startWidth: sidebarWidth,
    };
  }, [sidebarWidth]);

  const handleMouseUp = useCallback(() => {
    isResizing.current = false;
    dragStartInfo.current = null;
  }, []);

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (isResizing.current && dragStartInfo.current) {
      const deltaX = e.clientX - dragStartInfo.current.startX;
      const newWidth = dragStartInfo.current.startWidth + deltaX;

      if (newWidth > 200 && newWidth < 600) {
        setSidebarWidth(newWidth);
      }
    }
  }, []);

  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [handleMouseMove, handleMouseUp]);

  const onExportSRT = async () => {
    try {
      const srtContent = await handleExport();
      const blob = new Blob([srtContent], {
        type: "text/plain;charset=utf-8",
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "subtitles.srt";
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
      notify.success('SRT导出成功！');
    } catch (error: any) {
      notify.error(error.message || '导出SRT时发生未知错误');
    }
  };

  const onExportToDavinci = async () => {
    const result = await handleExportToDavinci();
    if (result.success) {
      notify.success(result.message);
    } else {
      notify.error(result.message);
    }
  };

  const renderSidebar = () => {
    const commonPaperStyles = {
      width: '100%',
      height: '100%',
      backgroundColor: '#252526',
      borderRadius: 0,
      borderRight: '1px solid #3c3c3c',
    };

    switch (activeView) {
      case 'explorer':
        return (
          <FileExplorer
            fetchSubtitleTracks={fetchSubtitleTracks}
            loading={loading}
            onTrackSelect={handleTrackSelect}
          />
        );
      case 'search':
        return (
          <Paper sx={commonPaperStyles}>
            <Box sx={{ p: 1.5, borderBottom: '1px solid #3c3c3c' }}>
              <Typography variant="h6" sx={{ color: '#cccccc' }}>
                Search
              </Typography>
            </Box>
            <Box sx={{ p: 1.5 }}>
              <FindReplace
                searchQuery={searchQuery}
                replaceQuery={replaceQuery}
                showReplace={showReplace}
                matchCase={matchCase}
                matchWholeWord={matchWholeWord}
                useRegex={useRegex}
                onSearchChange={handleSearchChange}
                onReplaceChange={handleReplaceChange}
                onReplaceAll={handleReplaceAll}
                onToggleShowReplace={toggleShowReplace}
                onToggleMatchCase={toggleMatchCase}
                onToggleMatchWholeWord={toggleMatchWholeWord}
                onToggleUseRegex={toggleUseRegex}
              />
              {searchQuery && (
                <SearchResults
                  subtitles={filteredSubtitles}
                  onResultClick={handleResultClick}
                />
              )}
            </Box>
          </Paper>
        );
      case 'optimizer':
        return <OptimizerSidebar />;
      case 'git':
        return (
          <Paper sx={commonPaperStyles}>
            <Box sx={{ p: 1.5, borderBottom: '1px solid #3c3c3c' }}>
              <Typography variant="h6" sx={{ color: '#cccccc' }}>
                Source Control
              </Typography>
            </Box>
            <Box sx={{ p: 1.5, display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Button
                variant="contained"
                color="primary"
                onClick={onExportSRT}
                disabled={subtitles.length === 0}
                startIcon={<Download size={16} />}
              >
                导出SRT
              </Button>
              <Button
                variant="contained"
                color="primary"
                onClick={onExportToDavinci}
                disabled={subtitles.length === 0}
                startIcon={<Send size={16} />}
              >
                导出至达芬奇
              </Button>
            </Box>
          </Paper>
        );
      default:
        return null;
    }
  };

  return (
    <ThemeProvider>
      <Box
        sx={{
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          backgroundColor: '#1e1e1e',
          color: '#cccccc',
        }}
      >
        {/* Title Bar */}
        <TitleBar />
        {/* Main Content Area */}
        <Box sx={{ flex: 1, display: 'flex', minHeight: 0 }}>
          {/* Activity Bar */}
          <ActivityBar />

          {/* Sidebar */}
          <Box
            sx={{
              width: isSidebarOpen ? sidebarWidth : 0,
              height: '100%',
              transition: 'width 0.3s ease-in-out',
              overflow: 'hidden',
            }}
          >
            {renderSidebar()}
          </Box>

          {/* Resizer */}
          {isSidebarOpen && (
            <Box
              onMouseDown={handleMouseDown}
              sx={{
                width: '1px',
                cursor: 'col-resize',
                backgroundColor: 'transparent',
                height: '100%',
                transition: 'background-color 0.2s ease-in-out, width 0.2s ease-in-out',
                '&:hover': {
                  backgroundColor: '#007acc',
                  width: '3px',
                  transform: 'translateX(-1px)'
                },
              }}
            />
          )}

          {/* Editor Area */}
          <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
            <SubtitleEditorPage
              jumpToSubtitleId={jumpToSubtitleId}
              onRowClick={handleRowClick}
            />
          </Box>
        </Box>

        {/* Status Bar */}
        <StatusBar />
      </Box>
    </ThemeProvider>
  );
}

import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
} from '@mui/material';
import { ChevronDown } from 'lucide-react';
import { FillerWordRemover } from '../FillerWordRemover';

export function OptimizerSidebar() {
  return (
    <Box sx={{ p: 1, height: '100%', color: '#cccccc', backgroundColor: '#252526' }}>
      <Box sx={{ p: 1.5, borderBottom: '1px solid #3c3c3c', mb: 1 }}>
        <Typography variant="h6" sx={{ color: '#cccccc' }}>
          Optimizer
        </Typography>
      </Box>
      <Accordion defaultExpanded sx={{ backgroundColor: '#2c2c2c', color: '#cccccc' }}>
        <AccordionSummary
          expandIcon={<ChevronDown size={18} />}
          sx={{
            '& .MuiAccordionSummary-content': {
              fontWeight: 'bold',
            },
          }}
        >
          <Typography>Simple Mode</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <FillerWordRemover />
        </AccordionDetails>
      </Accordion>
    </Box>
  );
}

import { Box, Typography, Paper } from '@mui/material';
import { Circle } from 'lucide-react';
import { useMemo } from 'react';

import { useDataStore } from '../../stores/useDataStore';
import { useUIStore } from '../../stores/useUIStore';
import { JumpModeSelector } from './JumpModeSelector';

export function StatusBar() {
  const selectedSubtitleId = useUIStore((state) => state.selectedSubtitleId);
  const {
    connectionStatus,
    errorMessage,
    subtitles,
    projectInfo,
  } = useDataStore();

  const statusConfig = {
    connected: { label: '已连接', color: 'success', iconColor: '#4ade80' },
    connecting: { label: '连接中...', color: 'warning', iconColor: '#fde047' },
    error: { label: `错误: ${errorMessage}`, color: 'error', iconColor: '#f87171' },
    disconnected: { label: '等待连接...', color: 'default', iconColor: '#9ca3af' },
  } as const;

  const { label, iconColor } = statusConfig[connectionStatus];
  const [totalChars, originalChars] = useMemo(() => {
    const total = subtitles.reduce((acc, s) => acc + s.text.length, 0);
    const original = subtitles.reduce((acc, s) => acc + s.originalText.length, 0);
    return [total, original];
  }, [subtitles]);
  const selectedStartTimecode = useMemo(() => {
    return subtitles.find(s => s.id === selectedSubtitleId)?.startTimecode ?? 'N/A';
  }, [subtitles, selectedSubtitleId]);

  return (
    <Paper
      sx={{
        height: 24,
        backgroundColor: '#252526',
        color: '#cccccc',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        paddingX: 1.5,
        borderRadius: 0,
        borderTop: '1px solid #3c3c3c',
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, flex: 1 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
          <Circle size={8} color={iconColor} />
          <Typography variant="caption" sx={{ fontSize: '0.6rem' }}>
            {label}
          </Typography>
        </Box>
        <Typography variant="caption" sx={{ fontSize: '0.6rem' }}>
          Project: {projectInfo?.projectName || 'N/A'}
        </Typography>
        <Typography variant="caption" sx={{ fontSize: '0.6rem' }}>
          Timeline: {projectInfo?.timelineName || 'N/A'}
        </Typography>
      </Box>

      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
        }}
      >
        <JumpModeSelector />
      </Box>

      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-end', gap: 2, flex: 1 }}>
        <Typography variant="caption" sx={{ fontSize: '0.6rem' }}>
          Total Chars: {totalChars} ({originalChars})
        </Typography>
        <Typography variant="caption" sx={{ fontSize: '0.6rem' }}>
          Selected: {selectedSubtitleId ?? 'N/A'}
        </Typography>
        <Typography variant="caption" sx={{ fontSize: '0.6rem' }}>
          Timecode: {selectedStartTimecode}
        </Typography>
      </Box>
    </Paper>
  );
}

import { createTheme, ThemeProvider as MuiThemeProvider } from '@mui/material/styles';
import { CssBaseline } from '@mui/material';
import { ReactNode } from 'react';

const theme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#1e1e1e',
      paper: '#252526',
    },
    text: {
      primary: '#cccccc',
      secondary: '#969696',
    },
    primary: {
      main: '#007acc',
      contrastText: '#ffffff',
    },
    secondary: {
      main: '#37373d',
    },
    divider: '#3c3c3c',
    action: {
      hover: '#37373d',
      selected: '#37373d',
    },
  },
  typography: {
    fontSize: 12,
    fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
    h6: {
      fontSize: '0.75rem',
      textTransform: 'uppercase',
      letterSpacing: '0.05em',
      fontWeight: 400,
    },
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: '#1e1e1e',
          color: '#cccccc',
          fontSize: '12px',
        },
      },
